{
  "definitions": {},
  "$schema": "http://json-schema.org/draft-07/schema#",
  "$id": "http://example.com/root.json",
  "type": "array",
  "title": "The Root Schema",
  "items": {
    "$id": "#/items",
    "type": "object",
    "title": "The Items Schema",
    "required": [
      "id",
      "hrId",
      "hrCount",
      "urn",
      "version",
      "createdAt",
      "lastModifiedAt",
      "customer",
      "billableCustomerId",
      "managedBy",
      "poContact",
      "status",
      "originalDue",
      "originalDoNotDeliverBefore",
      "firstDue",
      "lastDue",
      "progressHistory",
      "source",
      "description",
      "invoiceId",
      "externalReference",
      "received",
      "createdBy",
      "createdByName",
      "completedAt",
      "workabilityStart",
      "workflows",
      "details",
      "jobsProgress"
    ],
    "properties": {
      "id": {
        "$id": "#/items/properties/id",
        "type": "string",
        "title": "The Id Schema",
        "default": "",
        "examples": [
          "a457be94-aefe-437a-9dcf-37e048a3f2c5"
        ],
        "pattern": "^(.*)$"
      },
      "hrId": {
        "$id": "#/items/properties/hrId",
        "type": "string",
        "title": "The Hrid Schema",
        "default": "",
        "examples": [
          "W5809"
        ],
        "pattern": "^(.*)$"
      },
      "hrCount": {
        "$id": "#/items/properties/hrCount",
        "type": "integer",
        "title": "The Hrcount Schema",
        "default": 0,
        "examples": [
          5809
        ]
      },
      "urn": {
        "$id": "#/items/properties/urn",
        "type": "string",
        "title": "The Urn Schema",
        "default": "",
        "examples": [
          "urn:deluxe:one-orders:order:a457be94-aefe-437a-9dcf-37e048a3f2c5"
        ],
        "pattern": "^(.*)$"
      },
      "version": {
        "$id": "#/items/properties/version",
        "type": "integer",
        "title": "The Version Schema",
        "default": 0,
        "examples": [
          1
        ]
      },
      "createdAt": {
        "$id": "#/items/properties/createdAt",
        "type": "string",
        "title": "The Createdat Schema",
        "default": "",
        "examples": [
          "2018-09-15T19:01:50.4539828Z"
        ],
        "pattern": "^(.*)$"
      },
      "lastModifiedAt": {
        "$id": "#/items/properties/lastModifiedAt",
        "type": "string",
        "title": "The Lastmodifiedat Schema",
        "default": "",
        "examples": [
          "2018-09-15T19:01:50.453985Z"
        ],
        "pattern": "^(.*)$"
      },
      "customer": {
        "$id": "#/items/properties/customer",
        "type": "string",
        "title": "The Customer Schema",
        "default": "",
        "examples": [
          "e3b59a42"
        ],
        "pattern": "^(.*)$"
      },
      "billableCustomerId": {
        "$id": "#/items/properties/billableCustomerId",
        "type": "null",
        "title": "The Billablecustomerid Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "managedBy": {
        "$id": "#/items/properties/managedBy",
        "type": "null",
        "title": "The Managedby Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "poContact": {
        "$id": "#/items/properties/poContact",
        "type": "null",
        "title": "The Pocontact Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "status": {
        "$id": "#/items/properties/status",
        "type": "string",
        "title": "The Status Schema",
        "default": "",
        "examples": [
          "RECEIVED"
        ],
        "pattern": "^(.*)$"
      },
      "originalDue": {
        "$id": "#/items/properties/originalDue",
        "type": "string",
        "title": "The Originaldue Schema",
        "default": "",
        "examples": [
          "2018-09-15T23:01:43.038Z"
        ],
        "pattern": "^(.*)$"
      },
      "originalDoNotDeliverBefore": {
        "$id": "#/items/properties/originalDoNotDeliverBefore",
        "type": "string",
        "title": "The Originaldonotdeliverbefore Schema",
        "default": "",
        "examples": [
          "2018-09-16T19:01:42.444Z"
        ],
        "pattern": "^(.*)$"
      },
      "firstDue": {
        "$id": "#/items/properties/firstDue",
        "type": "null",
        "title": "The Firstdue Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "lastDue": {
        "$id": "#/items/properties/lastDue",
        "type": "null",
        "title": "The Lastdue Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "progressHistory": {
        "$id": "#/items/properties/progressHistory",
        "type": "array",
        "title": "The Progresshistory Schema"
      },
      "source": {
        "$id": "#/items/properties/source",
        "type": "string",
        "title": "The Source Schema",
        "default": "",
        "examples": [
          "Ui"
        ],
        "pattern": "^(.*)$"
      },
      "description": {
        "$id": "#/items/properties/description",
        "type": "string",
        "title": "The Description Schema",
        "default": "",
        "examples": [
          "DraftOrder xUnit 12:01:42 updated at 12:01:43"
        ],
        "pattern": "^(.*)$"
      },
      "invoiceId": {
        "$id": "#/items/properties/invoiceId",
        "type": "null",
        "title": "The Invoiceid Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "externalReference": {
        "$id": "#/items/properties/externalReference",
        "type": "null",
        "title": "The Externalreference Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "received": {
        "$id": "#/items/properties/received",
        "type": "string",
        "title": "The Received Schema",
        "default": "",
        "examples": [
          "2018-09-15T19:01:50.4535151Z"
        ],
        "pattern": "^(.*)$"
      },
      "createdBy": {
        "$id": "#/items/properties/createdBy",
        "type": "string",
        "title": "The Createdby Schema",
        "default": "",
        "examples": [
          "service-account-deluxe.sfera-test@placeholder.org"
        ],
        "pattern": "^(.*)$"
      },
      "createdByName": {
        "$id": "#/items/properties/createdByName",
        "type": "null",
        "title": "The Createdbyname Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "completedAt": {
        "$id": "#/items/properties/completedAt",
        "type": "null",
        "title": "The Completedat Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "workabilityStart": {
        "$id": "#/items/properties/workabilityStart",
        "type": "null",
        "title": "The Workabilitystart Schema",
        "default": null,
        "examples": [
          null
        ]
      },
      "workflows": {
        "$id": "#/items/properties/workflows",
        "type": "array",
        "title": "The Workflows Schema"
      },
      "details": {
        "$id": "#/items/properties/details",
        "type": "object",
        "title": "The Details Schema",
        "required": [
          "test"
        ],
        "properties": {
          "test": {
            "$id": "#/items/properties/details/properties/test",
            "type": "string",
            "title": "The Test Schema",
            "default": "",
            "examples": [
              "item"
            ],
            "pattern": "^(.*)$"
          }
        }
      },
      "jobsProgress": {
        "$id": "#/items/properties/jobsProgress",
        "type": "array",
        "title": "The Jobsprogress Schema"
      }
    }
  }
}
